function flatItem(base, key, src, dest) {
     if (typeof src === 'undefined') {
         return;
     }
     const deepKey = base.length ? base + '.' + key : key;
     // console.log(`base=${base} key=${key} deepKey=${deepKey}`);
     if (typeof src === 'object') {
        if (Array.isArray(src)) {
            src.forEach((val, idx) => flatItem(deepKey, idx, val, dest));
        } else {
            Object.entries(src).forEach(([key, val]) => flatItem(deepKey, key, val, dest))
        }
     } else {
        dest[deepKey] = src;
     }
}

