    // Find all subscriptions for the specified endpoint
    foundSubscriptionItems = pushSubscriptions.filter( subscriptionItem => {
        return subscriptionItem.endpoint == msg.payload.endpoint;
    })
}

let totalSubscriptionLength = pushSubscriptions.length;

switch (msg.topic) {
    case 'subscription_new':
    case 'subscription_existing':
        var subscription = msg.payload;
        if (foundSubscriptionItems.length === 0) {
            pushSubscriptions.push(subscription);
            result = 'Subscription registered: ' + subscription.endpoint
        } else {
            result = 'Subscription was already registered: ' + subscription.endpoint
        }

        msg.statusCode = 200;
        break;

    case 'unsubscription':
        var unsubscription = msg.payload;
        if(foundSubscriptionItems.length === 0) {
            result = 'Subscription was not found: ' + unsubscription.endpoint
        } else {
            pushSubscriptions = pushSubscriptions.filter(subscriptionItem => {
                return subscriptionItem.endpoint !== unsubscription.endpoint
            })
            result = 'Subscription unregistered: ' + unsubscription.endpoint
        }

        msg.statusCode = 200;
        break;
    case 'reset':
        // All push subscriptions will be removed!!!!!!!!!
        // Make sure you know what you are doing, because you cannot send notifications to these endpoints anymore!!!!!!!!!
        pushSubscriptions = [];
        break;
    default:
