const mw = 18.01534;    // molar mass of water g/mol
const r = 8.31447215;   // Universal gas constant J/mol/K

const tempc = Number(msg.payload.tempc);
const humidity = Number(msg.payload.humidity);

const absolute_humidity = Number((6.112 * Math.exp((17.67 * tempc) /
    (tempc + 243.5)) * humidity * mw) /
    ((273.15 + tempc) * r));
const dew_point = Number((243.5 * (Math.log(humidity / 100) + ((17.67 * tempc) /
    (243.5 + tempc))) / (17.67 - Math.log(humidity / 100) -
        ((17.67 * tempc) / (243.5 + tempc)))));

msg.payload.absolute_humidity = absolute_humidity;
msg.payload.dew_point = dew_point;

return msg;

